INSERT INTO `proc` VALUES ('sys', 'ps_setup_show_enabled_consumers', 'PROCEDURE', 'ps_setup_show_enabled_consumers', 'SQL', 'READS_SQL_DATA', 'YES', 'INVOKER', '', '', , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Shows all currently enabled consumers.\n             Parameters\n             None\n             Example\n             mysql> CALL sys.ps_setup_show_enabled_consumers();\n             +---------------------------+\n             | enabled_consumers         |\n             +---------------------------+\n             | events_statements_current |\n             | global_instrumentation    |\n             | thread_instrumentation    |\n             | statements_digest         |\n             +---------------------------+\n             4 rows in set (0.05 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'create_synonym_db', 'PROCEDURE', 'create_synonym_db', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', 0x0A2020202020202020494E20696E5F64625F6E616D652056415243484152283634292C0A2020202020202020494E20696E5F73796E6F6E796D2056415243484152283634290A20202020, '', , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Takes a source database name and synonym name, and then creates the\n             synonym database with views that point to all of the tables within\n             the source database.\n             Useful for creating a \"ps\" synonym for \"performance_schema\",\n             or \"is\" instead of \"information_schema\", for example.\n             Parameters\n             in_db_name (VARCHAR(64)):\n               The database name that you would like to create a synonym for.\n             in_synonym (VARCHAR(64)):\n               The database synonym name.\n             Example\n             mysql> SHOW DATABASES;\n             +--------------------+\n             | Database           |\n             +--------------------+\n             | information_schema |\n             | mysql              |\n             | performance_schema |\n             | sys                |\n             | test               |\n             +--------------------+\n             5 rows in set (0.00 sec)\n             mysql> CALL sys.create_synonym_db(\'performance_schema\', \'ps\');\n             +---------------------------------------+\n             | summary                               |\n             +---------------------------------------+\n             | Created 74 views in the `ps` database |\n             +---------------------------------------+\n             1 row in set (8.57 sec)\n             Query OK, 0 rows affected (8.57 sec)\n             mysql> SHOW DATABASES;\n             +--------------------+\n             | Database           |\n             +--------------------+\n             | information_schema |\n             | mysql              |\n             | performance_schema |\n             | ps                 |\n             | sys                |\n             | test               |\n             +--------------------+\n             6 rows in set (0.00 sec)\n             mysql> SHOW FULL TABLES FROM ps;\n             +------------------------------------------------------+------------+\n             | Tables_in_ps                                         | Table_type |\n             +------------------------------------------------------+------------+\n             | accounts                                             | VIEW       |\n             | cond_instances                                       | VIEW       |\n             | events_stages_current                                | VIEW       |\n             | events_stages_history                                | VIEW       |\n             ...\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'diagnostics', 'PROCEDURE', 'diagnostics', 'SQL', 'READS_SQL_DATA', 'NO', 'INVOKER', 0x0A2020202020202020494E20696E5F6D61785F72756E74696D6520696E7420756E7369676E65642C20494E20696E5F696E74657276616C20696E7420756E7369676E65642C0A2020202020202020494E20696E5F6175746F5F636F6E66696720656E756D20282763757272656E74272C20276D656469756D272C202766756C6C27290A20202020, '', , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Create a report of the current status of the server for diagnostics purposes. Data collected includes (some items depends on versions and settings):\n                * The GLOBAL VARIABLES\n                * Several sys schema views including metrics or equivalent (depending on version and settings)\n                * Queries in the 95th percentile\n                * Several ndbinfo views for MySQL Cluster\n                * Replication (both master and slave) information.\n             Some of the sys schema views are calculated as initial (optional), overall, delta:\n                * The initial view is the content of the view at the start of this procedure.\n                  This output will be the same as the the start values used for the delta view.\n                  The initial view is included if @sys.diagnostics.include_raw = \'ON\'.\n                * The overall view is the content of the view at the end of this procedure.\n                  This output is the same as the end values used for the delta view.\n                  The overall view is always included.\n                * The delta view is the difference from the beginning to the end. Note that for min and max values\n                  they are simply the min or max value from the end view respectively, so does not necessarily reflect\n                  the minimum/maximum value in the monitored period.\n                  Note: except for the metrics views the delta is only calculation between the first and last outputs.\n             Requires the SUPER privilege for \"SET sql_log_bin = 0;\".\n             Versions supported:\n                * MySQL 5.6: 5.6.10 and later\n                * MySQL 5.7: 5.7.9 and later\n             Parameters\n             in_max_runtime (INT UNSIGNED):\n               The maximum time to keep collecting data.\n               Use NULL to get the default which is 60 seconds, otherwise enter a value greater than 0.\n             in_interval (INT UNSIGNED):\n               How long to sleep between data collections.\n               Use NULL to get the default which is 30 seconds, otherwise enter a value greater than 0.\n             in_auto_config (ENUM(\'current\', \'medium\', \'full\'))\n               Automatically enable Performance Schema instruments and consumers.\n               NOTE: The more that are enabled, the more impact on the performance.\n               Supported values are:\n                  * current - use the current settings.\n                  * medium - enable some settings.\n                  * full - enables all settings. This will have a big impact on the\n                           performance - be careful using this option.\n               If another setting the \'current\' is chosen, the current settings\n               are restored at the end of the procedure.\n             Configuration Options\n             sys.diagnostics.allow_i_s_tables\n               Specifies whether it is allowed to do table scan queries on information_schema.TABLES. This can be expensive if there\n               are many tables. Set to \'ON\' to allow, \'OFF\' to not allow.\n               Default is \'OFF\'.\n             sys.diagnostics.include_raw\n               Set to \'ON\' to include the raw data (e.g. the original output of \"SELECT * FROM sys.metrics\").\n               Use this to get the initial values of the various views.\n               Default is \'OFF\'.\n             sys.statement_truncate_len\n               How much of queries in the process list output to include.\n               Default is 64.\n             sys.debug\n               Whether to provide debugging output.\n               Default is \'OFF\'. Set to \'ON\' to include.\n             Example\n             To create a report and append it to the file diag.out:\n             mysql> TEE diag.out;\n             mysql> CALL sys.diagnostics(120, 30, \'current\');\n             ...\n             mysql> NOTEE;\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'ps_truncate_all_tables', 'PROCEDURE', 'ps_truncate_all_tables', 'SQL', 'MODIFIES_SQL_DATA', 'YES', 'INVOKER', 0x0A2020202020202020494E20696E5F766572626F736520424F4F4C45414E0A20202020, '', , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Truncates all summary tables within Performance Schema,\n             resetting all aggregated instrumentation as a snapshot.\n             Parameters\n             in_verbose (BOOLEAN):\n               Whether to print each TRUNCATE statement before running\n             Example\n             mysql> CALL sys.ps_truncate_all_tables(false);\n             +---------------------+\n             | summary             |\n             +---------------------+\n             | Truncated 44 tables |\n             +---------------------+\n             1 row in set (0.10 sec)\n             Query OK, 0 rows affected (0.10 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'ps_is_instrument_default_enabled', 'FUNCTION', 'ps_is_instrument_default_enabled', 'SQL', 'READS_SQL_DATA', 'YES', 'INVOKER', 0x696E5F696E737472756D656E74205641524348415228313238290A20202020, 0x656E756D2827594553272C274E4F2729204348415253455420757466386D623320434F4C4C41544520757466386D62335F67656E6572616C5F6369, , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Returns whether an instrument is enabled by default in this version of MySQL.\n             Parameters\n             in_instrument VARCHAR(128):\n               The instrument to check.\n             Returns\n             ENUM(\'YES\', \'NO\')\n             Example\n             mysql> SELECT sys.ps_is_instrument_default_enabled(\'statement/sql/select\');\n             +--------------------------------------------------------------+\n             | sys.ps_is_instrument_default_enabled(\'statement/sql/select\') |\n             +--------------------------------------------------------------+\n             | YES                                                          |\n             +--------------------------------------------------------------+\n             1 row in set (0.00 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'ps_statement_avg_latency_histogram', 'PROCEDURE', 'ps_statement_avg_latency_histogram', 'SQL', 'READS_SQL_DATA', 'NO', 'INVOKER', '', '', , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Outputs a textual histogram graph of the average latency values\n             across all normalized queries tracked within the Performance Schema\n             events_statements_summary_by_digest table.\n             Can be used to show a very high level picture of what kind of\n             latency distribution statements running within this instance have.\n             Parameters\n             None.\n             Example\n             mysql> CALL sys.ps_statement_avg_latency_histogram()\\G\n             *************************** 1. row ***************************\n             Performance Schema Statement Digest Average Latency Histogram:\n               . = 1 unit\n               * = 2 units\n               # = 3 units\n             (0 - 38ms)     240 | ################################################################################\n             (38 - 77ms)    38  | ......................................\n             (77 - 115ms)   3   | ...\n             (115 - 154ms)  62  | *******************************\n             (154 - 192ms)  3   | ...\n             (192 - 231ms)  0   |\n             (231 - 269ms)  0   |\n             (269 - 307ms)  0   |\n             (307 - 346ms)  0   |\n             (346 - 384ms)  1   | .\n             (384 - 423ms)  1   | .\n             (423 - 461ms)  0   |\n             (461 - 499ms)  0   |\n             (499 - 538ms)  0   |\n             (538 - 576ms)  0   |\n             (576 - 615ms)  1   | .\n               Total Statements: 350; Buckets: 16; Bucket Size: 38 ms;\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'ps_trace_statement_digest', 'PROCEDURE', 'ps_trace_statement_digest', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', , '', , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Traces all instrumentation within Performance Schema for a specific\n             Statement Digest.\n             When finding a statement of interest within the\n             performance_schema.events_statements_summary_by_digest table, feed\n             the DIGEST MD5 value in to this procedure, set how long to poll for,\n             and at what interval to poll, and it will generate a report of all\n             statistics tracked within Performance Schema for that digest for the\n             interval.\n             It will also attempt to generate an EXPLAIN for the longest running\n             example of the digest during the interval. Note this may fail, as:\n                * Performance Schema truncates long SQL_TEXT values (and hence the\n                  EXPLAIN will fail due to parse errors)\n                * the default schema is sys (so tables that are not fully qualified\n                  in the query may not be found)\n                * some queries such as SHOW are not supported in EXPLAIN.\n             When the EXPLAIN fails, the error will be ignored and no EXPLAIN\n             output generated.\n             Requires the SUPER privilege for \"SET sql_log_bin = 0;\".\n             Parameters\n             in_digest (VARCHAR(32)):\n               The statement digest identifier you would like to analyze\n             in_runtime (INT):\n               The number of seconds to run analysis for\n             in_interval (DECIMAL(2,2)):\n               The interval (in seconds, may be fractional) at which to try\n               and take snapshots\n             in_start_fresh (BOOLEAN):\n               Whether to TRUNCATE the events_statements_history_long and\n               events_stages_history_long tables before starting\n             in_auto_enable (BOOLEAN):\n               Whether to automatically turn on required consumers\n             Example\n             mysql> call ps_trace_statement_digest(\'891ec6860f98ba46d89dd20b0c03652c\', 10, 0.1, true, true);\n             +--------------------+\n             | SUMMARY STATISTICS |\n             +--------------------+\n             | SUMMARY STATISTICS |\n             +--------------------+\n             1 row in set (9.11 sec)\n             +------------+-----------+-----------+-----------+---------------+------------+------------+\n             | executions | exec_time | lock_time | rows_sent | rows_examined | tmp_tables | full_scans |\n             +------------+-----------+-----------+-----------+---------------+------------+------------+\n             |         21 | 4.11 ms   | 2.00 ms   |         0 |            21 |          0 |          0 |\n             +------------+-----------+-----------+-----------+---------------+------------+------------+\n             1 row in set (9.11 sec)\n             +------------------------------------------+-------+-----------+\n             | event_name                               | count | latency   |\n             +------------------------------------------+-------+-----------+\n             | stage/sql/checking query cache for query |    16 | 724.37 us |\n             | stage/sql/statistics                     |    16 | 546.92 us |\n             | stage/sql/freeing items                  |    18 | 520.11 us |\n             | stage/sql/init                           |    51 | 466.80 us |\n             ...\n             | stage/sql/cleaning up                    |    18 | 11.92 us  |\n             | stage/sql/executing                      |    16 | 6.95 us   |\n             +------------------------------------------+-------+-----------+\n             17 rows in set (9.12 sec)\n             +---------------------------+\n             | LONGEST RUNNING STATEMENT |\n             +---------------------------+\n             | LONGEST RUNNING STATEMENT |\n             +---------------------------+\n             1 row in set (9.16 sec)\n             +-----------+-----------+-----------+-----------+---------------+------------+-----------+\n             | thread_id | exec_time | lock_time | rows_sent | rows_examined | tmp_tables | full_scan |\n             +-----------+-----------+-----------+-----------+---------------+------------+-----------+\n             |    166646 | 618.43 us | 1.00 ms   |         0 |             1 |          0 |         0 |\n             +-----------+-----------+-----------+-----------+---------------+------------+-----------+\n             1 row in set (9.16 sec)\n             // Truncated for clarity...\n             +-----------------------------------------------------------------+\n             | sql_text                                                        |\n             +-----------------------------------------------------------------+\n             | select hibeventhe0_.id as id1382_, hibeventhe0_.createdTime ... |\n             +-----------------------------------------------------------------+\n             1 row in set (9.17 sec)\n             +------------------------------------------+-----------+\n             | event_name                               | latency   |\n             +------------------------------------------+-----------+\n             | stage/sql/init                           | 8.61 us   |\n             | stage/sql/Waiting for query cache lock   | 453.23 us |\n             | stage/sql/init                           | 331.07 ns |\n             | stage/sql/checking query cache for query | 43.04 us  |\n             ...\n             | stage/sql/freeing items                  | 30.46 us  |\n             | stage/sql/cleaning up                    | 662.13 ns |\n             +------------------------------------------+-----------+\n             18 rows in set (9.23 sec)\n             +----+-------------+--------------+-------+---------------+-----------+---------+-------------+------+-------+\n             | id | select_type | table        | type  | possible_keys | key       | key_len | ref         | rows | Extra |\n             +----+-------------+--------------+-------+---------------+-----------+---------+-------------+------+-------+\n             |  1 | SIMPLE      | hibeventhe0_ | const | fixedTime     | fixedTime | 775     | const,const |    1 | NULL  |\n             +----+-------------+--------------+-------+---------------+-----------+---------+-------------+------+-------+\n             1 row in set (9.27 sec)\n             Query OK, 0 rows affected (9.28 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'ps_trace_thread', 'PROCEDURE', 'ps_trace_thread', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', , '', , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Dumps all data within Performance Schema for an instrumented thread,\n             to create a DOT formatted graph file.\n             Each resultset returned from the procedure should be used for a complete graph\n             Requires the SUPER privilege for \"SET sql_log_bin = 0;\".\n             Parameters\n             in_thread_id (BIGINT UNSIGNED):\n               The thread that you would like a stack trace for\n             in_outfile  (VARCHAR(255)):\n               The filename the dot file will be written to\n             in_max_runtime (DECIMAL(20,2)):\n               The maximum time to keep collecting data.\n               Use NULL to get the default which is 60 seconds.\n             in_interval (DECIMAL(20,2)):\n               How long to sleep between data collections.\n               Use NULL to get the default which is 1 second.\n             in_start_fresh (BOOLEAN):\n               Whether to reset all Performance Schema data before tracing.\n             in_auto_setup (BOOLEAN):\n               Whether to disable all other threads and enable all consumers/instruments.\n               This will also reset the settings at the end of the run.\n             in_debug (BOOLEAN):\n               Whether you would like to include file:lineno in the graph\n             Example\n             mysql> CALL sys.ps_trace_thread(25, CONCAT(\'/tmp/stack-\', REPLACE(NOW(), \' \', \'-\'), \'.dot\'), NULL, NULL, TRUE, TRUE, TRUE);\n             +-------------------+\n             | summary           |\n             +-------------------+\n             | Disabled 1 thread |\n             +-------------------+\n             1 row in set (0.00 sec)\n             +---------------------------------------------+\n             | Info                                        |\n             +---------------------------------------------+\n             | Data collection starting for THREAD_ID = 25 |\n             +---------------------------------------------+\n             1 row in set (0.03 sec)\n             +-----------------------------------------------------------+\n             | Info                                                      |\n             +-----------------------------------------------------------+\n             | Stack trace written to /tmp/stack-2014-02-16-21:18:41.dot |\n             +-----------------------------------------------------------+\n             1 row in set (60.07 sec)\n             +-------------------------------------------------------------------+\n             | Convert to PDF                                                    |\n             +-------------------------------------------------------------------+\n             | dot -Tpdf -o /tmp/stack_25.pdf /tmp/stack-2014-02-16-21:18:41.dot |\n             +-------------------------------------------------------------------+\n             1 row in set (60.07 sec)\n             +-------------------------------------------------------------------+\n             | Convert to PNG                                                    |\n             +-------------------------------------------------------------------+\n             | dot -Tpng -o /tmp/stack_25.png /tmp/stack-2014-02-16-21:18:41.dot |\n             +-------------------------------------------------------------------+\n             1 row in set (60.07 sec)\n             +------------------+\n             | summary          |\n             +------------------+\n             | Enabled 1 thread |\n             +------------------+\n             1 row in set (60.32 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'format_path', 'FUNCTION', 'format_path', 'SQL', 'NO_SQL', 'YES', 'INVOKER', 0x696E5F70617468205641524348415228353132290A20202020, 0x766172636861722835313229204348415253455420757466386D623320434F4C4C41544520757466386D62335F67656E6572616C5F6369, , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Takes a raw path value, and strips out the datadir or tmpdir\n             replacing with @@datadir and @@tmpdir respectively.\n             Also normalizes the paths across operating systems, so backslashes\n             on Windows are converted to forward slashes\n             Parameters\n             path (VARCHAR(512)):\n               The raw file path value to format.\n             Returns\n             VARCHAR(512) CHARSET UTF8\n             Example\n             mysql> select @@datadir;\n             +-----------------------------------------------+\n             | @@datadir                                     |\n             +-----------------------------------------------+\n             | /Users/mark/sandboxes/SmallTree/AMaster/data/ |\n             +-----------------------------------------------+\n             1 row in set (0.06 sec)\n             mysql> select format_path(\'/Users/mark/sandboxes/SmallTree/AMaster/data/mysql/proc.MYD\') AS path;\n             +--------------------------+\n             | path                     |\n             +--------------------------+\n             | @@datadir/mysql/proc.MYD |\n             +--------------------------+\n             1 row in set (0.03 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'ps_is_instrument_default_timed', 'FUNCTION', 'ps_is_instrument_default_timed', 'SQL', 'READS_SQL_DATA', 'YES', 'INVOKER', 0x696E5F696E737472756D656E74205641524348415228313238290A20202020, 0x656E756D2827594553272C274E4F2729204348415253455420757466386D623320434F4C4C41544520757466386D62335F67656E6572616C5F6369, , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Returns whether an instrument is timed by default in this version of MySQL.\n             Parameters\n             in_instrument VARCHAR(128):\n               The instrument to check.\n             Returns\n             ENUM(\'YES\', \'NO\')\n             Example\n             mysql> SELECT sys.ps_is_instrument_default_timed(\'statement/sql/select\');\n             +------------------------------------------------------------+\n             | sys.ps_is_instrument_default_timed(\'statement/sql/select\') |\n             +------------------------------------------------------------+\n             | YES                                                        |\n             +------------------------------------------------------------+\n             1 row in set (0.00 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'list_drop', 'FUNCTION', 'list_drop', 'SQL', 'CONTAINS_SQL', 'YES', 'INVOKER', 0x696E5F6C69737420544558542C0A2020202020202020696E5F64726F705F76616C756520544558540A20202020, 0x74657874204348415253455420757466386D623320434F4C4C41544520757466386D62335F67656E6572616C5F6369, , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Takes a list, and a value to attempt to remove from the list, and returns the resulting list.\n             Useful for altering certain session variables, like sql_mode or optimizer_switch for instance.\n             Parameters\n             in_list (TEXT):\n               The comma separated list to drop a value from\n             in_drop_value (TEXT):\n               The value to drop from the input list\n             Returns\n             TEXT\n             Example\n             mysql> select @@sql_mode;\n             +-----------------------------------------------------------------------------------------------+\n             | @@sql_mode                                                                                    |\n             +-----------------------------------------------------------------------------------------------+\n             | ANSI_QUOTES,ONLY_FULL_GROUP_BY,STRICT_TRANS_TABLES,NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION |\n             +-----------------------------------------------------------------------------------------------+\n             1 row in set (0.00 sec)\n             mysql> set sql_mode = sys.list_drop(@@sql_mode, \'ONLY_FULL_GROUP_BY\');\n             Query OK, 0 rows affected (0.03 sec)\n             mysql> select @@sql_mode;\n             +----------------------------------------------------------------------------+\n             | @@sql_mode                                                                 |\n             +----------------------------------------------------------------------------+\n             | ANSI_QUOTES,STRICT_TRANS_TABLES,NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION |\n             +----------------------------------------------------------------------------+\n             1 row in set (0.00 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'ps_setup_disable_thread', 'PROCEDURE', 'ps_setup_disable_thread', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', 0x0A2020202020202020494E20696E5F636F6E6E656374696F6E5F696420424947494E540A20202020, '', , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Disable the given connection/thread in Performance Schema.\n             Parameters\n             in_connection_id (BIGINT):\n               The connection ID (PROCESSLIST_ID from performance_schema.threads\n               or the ID shown within SHOW PROCESSLIST)\n             Example\n             mysql> CALL sys.ps_setup_disable_thread(3);\n             +-------------------+\n             | summary           |\n             +-------------------+\n             | Disabled 1 thread |\n             +-------------------+\n             1 row in set (0.01 sec)\n             To disable the current connection:\n             mysql> CALL sys.ps_setup_disable_thread(CONNECTION_ID());\n             +-------------------+\n             | summary           |\n             +-------------------+\n             | Disabled 1 thread |\n             +-------------------+\n             1 row in set (0.00 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'ps_setup_disable_instrument', 'PROCEDURE', 'ps_setup_disable_instrument', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', 0x0A2020202020202020494E20696E5F7061747465726E205641524348415228313238290A20202020, '', , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Disables instruments within Performance Schema\n             matching the input pattern.\n             Parameters\n             in_pattern (VARCHAR(128)):\n               A LIKE pattern match (using \"%in_pattern%\") of events to disable\n             Example\n             To disable all mutex instruments:\n             mysql> CALL sys.ps_setup_disable_instrument(\'wait/synch/mutex\');\n             +--------------------------+\n             | summary                  |\n             +--------------------------+\n             | Disabled 155 instruments |\n             +--------------------------+\n             1 row in set (0.02 sec)\n             To disable just a specific TCP/IP based network IO instrument:\n             mysql> CALL sys.ps_setup_disable_instrument(\'wait/io/socket/sql/server_tcpip_socket\');\n             +------------------------+\n             | summary                |\n             +------------------------+\n             | Disabled 1 instruments |\n             +------------------------+\n             1 row in set (0.00 sec)\n             To disable all instruments:\n             mysql> CALL sys.ps_setup_disable_instrument(\'\');\n             +--------------------------+\n             | summary                  |\n             +--------------------------+\n             | Disabled 547 instruments |\n             +--------------------------+\n             1 row in set (0.01 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'ps_thread_account', 'FUNCTION', 'ps_thread_account', 'SQL', 'READS_SQL_DATA', 'NO', 'INVOKER', 0x696E5F7468726561645F696420424947494E5420554E5349474E45440A20202020, 0x74657874204348415253455420757466386D623320434F4C4C41544520757466386D62335F67656E6572616C5F6369, , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Return the user@host account for the given Performance Schema thread id.\n             Parameters\n             in_thread_id (BIGINT UNSIGNED):\n               The id of the thread to return the account for.\n             Example\n             mysql> select thread_id, processlist_user, processlist_host from performance_schema.threads where type = \'foreground\';\n              +-----------+------------------+------------------+\n              | thread_id | processlist_user | processlist_host |\n              +-----------+------------------+------------------+\n              |        23 | NULL             | NULL             |\n              |        30 | root             | localhost        |\n              |        31 | msandbox         | localhost        |\n              |        32 | msandbox         | localhost        |\n              +-----------+------------------+------------------+\n              4 rows in set (0.00 sec)\n              mysql> select sys.ps_thread_account(31);\n              +---------------------------+\n              | sys.ps_thread_account(31) |\n              +---------------------------+\n              | msandbox@localhost        |\n              +---------------------------+\n              1 row in set (0.00 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'ps_thread_id', 'FUNCTION', 'ps_thread_id', 'SQL', 'READS_SQL_DATA', 'NO', 'INVOKER', 0x696E5F636F6E6E656374696F6E5F696420424947494E5420554E5349474E45440A20202020, 0x626967696E742832302920756E7369676E6564, , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Return the Performance Schema THREAD_ID for the specified connection ID.\n             Parameters\n             in_connection_id (BIGINT UNSIGNED):\n               The id of the connection to return the thread id for. If NULL, the current\n               connection thread id is returned.\n             Example\n             mysql> SELECT sys.ps_thread_id(79);\n             +----------------------+\n             | sys.ps_thread_id(79) |\n             +----------------------+\n             |                   98 |\n             +----------------------+\n             1 row in set (0.00 sec)\n             mysql> SELECT sys.ps_thread_id(CONNECTION_ID());\n             +-----------------------------------+\n             | sys.ps_thread_id(CONNECTION_ID()) |\n             +-----------------------------------+\n             |                                98 |\n             +-----------------------------------+\n             1 row in set (0.00 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'ps_setup_enable_instrument', 'PROCEDURE', 'ps_setup_enable_instrument', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', 0x0A2020202020202020494E20696E5F7061747465726E205641524348415228313238290A20202020, '', , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Enables instruments within Performance Schema\n             matching the input pattern.\n             Parameters\n             in_pattern (VARCHAR(128)):\n               A LIKE pattern match (using \"%in_pattern%\") of events to enable\n             Example\n             To enable all mutex instruments:\n             mysql> CALL sys.ps_setup_enable_instrument(\'wait/synch/mutex\');\n             +-------------------------+\n             | summary                 |\n             +-------------------------+\n             | Enabled 155 instruments |\n             +-------------------------+\n             1 row in set (0.02 sec)\n             Query OK, 0 rows affected (0.02 sec)\n             To enable just a specific TCP/IP based network IO instrument:\n             mysql> CALL sys.ps_setup_enable_instrument(\'wait/io/socket/sql/server_tcpip_socket\');\n             +-----------------------+\n             | summary               |\n             +-----------------------+\n             | Enabled 1 instruments |\n             +-----------------------+\n             1 row in set (0.00 sec)\n             Query OK, 0 rows affected (0.00 sec)\n             To enable all instruments:\n             mysql> CALL sys.ps_setup_enable_instrument(\'\');\n             +-------------------------+\n             | summary                 |\n             +-------------------------+\n             | Enabled 547 instruments |\n             +-------------------------+\n             1 row in set (0.01 sec)\n             Query OK, 0 rows affected (0.01 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'ps_setup_enable_background_threads', 'PROCEDURE', 'ps_setup_enable_background_threads', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', '', '', , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Enable all background thread instrumentation within Performance Schema.\n             Parameters\n             None.\n             Example\n             mysql> CALL sys.ps_setup_enable_background_threads();\n             +-------------------------------+\n             | summary                       |\n             +-------------------------------+\n             | Enabled 18 background threads |\n             +-------------------------------+\n             1 row in set (0.00 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'ps_setup_enable_thread', 'PROCEDURE', 'ps_setup_enable_thread', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', 0x0A2020202020202020494E20696E5F636F6E6E656374696F6E5F696420424947494E540A20202020, '', , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Enable the given connection/thread in Performance Schema.\n             Parameters\n             in_connection_id (BIGINT):\n               The connection ID (PROCESSLIST_ID from performance_schema.threads\n               or the ID shown within SHOW PROCESSLIST)\n             Example\n             mysql> CALL sys.ps_setup_enable_thread(3);\n             +------------------+\n             | summary          |\n             +------------------+\n             | Enabled 1 thread |\n             +------------------+\n             1 row in set (0.01 sec)\n             To enable the current connection:\n             mysql> CALL sys.ps_setup_enable_thread(CONNECTION_ID());\n             +------------------+\n             | summary          |\n             +------------------+\n             | Enabled 1 thread |\n             +------------------+\n             1 row in set (0.00 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'sys_get_config', 'FUNCTION', 'sys_get_config', 'SQL', 'READS_SQL_DATA', 'YES', 'INVOKER', 0x696E5F7661726961626C655F6E616D65205641524348415228313238292C0A2020202020202020696E5F64656661756C745F76616C7565205641524348415228313238290A20202020, 0x766172636861722831323829204348415253455420757466386D623320434F4C4C41544520757466386D62335F67656E6572616C5F6369, , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Returns the value for the requested variable using the following logic:\n                1. If the option exists in sys.sys_config return the value from there.\n                2. Else fall back on the provided default value.\n             Notes for using sys_get_config():\n                * If the default value argument to sys_get_config() is NULL and case 2. is reached, NULL is returned.\n                  It is then expected that the caller is able to handle NULL for the given configuration option.\n                * The convention is to name the user variables @sys.<name of variable>. It is <name of variable> that\n                  is stored in the sys_config table and is what is expected as the argument to sys_get_config().\n                * If you want to check whether the configuration option has already been set and if not assign with\n                  the return value of sys_get_config() you can use IFNULL(...) (see example below). However this should\n                  not be done inside a loop (e.g. for each row in a result set) as for repeated calls where assignment\n                  is only needed in the first iteration using IFNULL(...) is expected to be significantly slower than\n                  using an IF (...) THEN ... END IF; block (see example below).\n             Parameters\n             in_variable_name (VARCHAR(128)):\n               The name of the config option to return the value for.\n             in_default_value (VARCHAR(128)):\n               The default value to return if the variable does not exist in sys.sys_config.\n             Returns\n             VARCHAR(128)\n             Example\n             mysql> SELECT sys.sys_get_config(\'statement_truncate_len\', 128) AS Value;\n             +-------+\n             | Value |\n             +-------+\n             | 64    |\n             +-------+\n             1 row in set (0.00 sec)\n             mysql> SET @sys.statement_truncate_len = IFNULL(@sys.statement_truncate_len, sys.sys_get_config(\'statement_truncate_len\', 64));\n             Query OK, 0 rows affected (0.00 sec)\n             IF (@sys.statement_truncate_len IS NULL) THEN\n                 SET @sys.statement_truncate_len = sys.sys_get_config(\'statement_truncate_len\', 64);\n             END IF;\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'format_statement', 'FUNCTION', 'format_statement', 'SQL', 'NO_SQL', 'YES', 'INVOKER', 0x73746174656D656E74204C4F4E47544558540A20202020, 0x6C6F6E6774657874204348415253455420757466386D623320434F4C4C41544520757466386D62335F67656E6572616C5F6369, , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Formats a normalized statement, truncating it if it is > 64 characters long by default.\n             To configure the length to truncate the statement to by default, update the `statement_truncate_len`\n             variable with `sys_config` table to a different value. Alternatively, to change it just for just\n             your particular session, use `SET @sys.statement_truncate_len := <some new value>`.\n             Useful for printing statement related data from Performance Schema from\n             the command line.\n             Parameters\n             statement (LONGTEXT):\n               The statement to format.\n             Returns\n             LONGTEXT\n             Example\n             mysql> SELECT sys.format_statement(digest_text)\n                 ->   FROM performance_schema.events_statements_summary_by_digest\n                 ->  ORDER by sum_timer_wait DESC limit 5;\n             +-------------------------------------------------------------------+\n             | sys.format_statement(digest_text)                                 |\n             +-------------------------------------------------------------------+\n             | CREATE SQL SECURITY INVOKER VI ... KE ? AND `variable_value` > ?  |\n             | CREATE SQL SECURITY INVOKER VI ... ait` IS NOT NULL , `esc` . ... |\n             | CREATE SQL SECURITY INVOKER VI ... ait` IS NOT NULL , `sys` . ... |\n             | CREATE SQL SECURITY INVOKER VI ...  , `compressed_size` ) ) DESC  |\n             | CREATE SQL SECURITY INVOKER VI ... LIKE ? ORDER BY `timer_start`  |\n             +-------------------------------------------------------------------+\n             5 rows in set (0.00 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'version_minor', 'FUNCTION', 'version_minor', 'SQL', 'NO_SQL', 'NO', 'INVOKER', '', 0x74696E79696E7428332920756E7369676E6564, 0x424547494E0A2020202052455455524E20535542535452494E475F494E44455828535542535452494E475F494E44455828535542535452494E475F494E4445582856455253494F4E28292C20272D272C2031292C20272E272C2032292C20272E272C202D31293B0A454E44, 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Returns the minor (release series) version of MySQL Server.\n             Returns\n             TINYINT UNSIGNED\n             Example\n             mysql> SELECT VERSION(), sys.server_minor();\n             +--------------------------------------+---------------------+\n             | VERSION()                            | sys.version_minor() |\n             +--------------------------------------+---------------------+\n             | 5.7.9-enterprise-commercial-advanced | 7                   |\n             +--------------------------------------+---------------------+\n             1 row in set (0.00 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', 0x424547494E0A2020202052455455524E20535542535452494E475F494E44455828535542535452494E475F494E44455828535542535452494E475F494E4445582856455253494F4E28292C20272D272C2031292C20272E272C2032292C20272E272C202D31293B0A454E44, 'NONE');
INSERT INTO `proc` VALUES ('sys', 'extract_schema_from_file_name', 'FUNCTION', 'extract_schema_from_file_name', 'SQL', 'NO_SQL', 'YES', 'INVOKER', 0x70617468205641524348415228353132290A20202020, 0x7661726368617228363429204348415253455420757466386D623320434F4C4C41544520757466386D62335F67656E6572616C5F6369, 0x424547494E0A2020202052455455524E204C45465428535542535452494E475F494E44455828535542535452494E475F494E444558285245504C41434528706174682C20275C5C272C20272F27292C20272F272C202D32292C20272F272C2031292C203634293B0A454E44, 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Takes a raw file path, and attempts to extract the schema name from it.\n             Useful for when interacting with Performance Schema data\n             concerning IO statistics, for example.\n             Currently relies on the fact that a table data file will be within a\n             specified database directory (will not work with partitions or tables\n             that specify an individual DATA_DIRECTORY).\n             Parameters\n             path (VARCHAR(512)):\n               The full file path to a data file to extract the schema name from.\n             Returns\n             VARCHAR(64)\n             Example\n             mysql> SELECT sys.extract_schema_from_file_name(\'/var/lib/mysql/employees/employee.ibd\');\n             +----------------------------------------------------------------------------+\n             | sys.extract_schema_from_file_name(\'/var/lib/mysql/employees/employee.ibd\') |\n             +----------------------------------------------------------------------------+\n             | employees                                                                  |\n             +----------------------------------------------------------------------------+\n             1 row in set (0.00 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', 0x424547494E0A2020202052455455524E204C45465428535542535452494E475F494E44455828535542535452494E475F494E444558285245504C41434528706174682C20275C272C20272F27292C20272F272C202D32292C20272F272C2031292C203634293B0A454E44, 'NONE');
INSERT INTO `proc` VALUES ('sys', 'format_time', 'FUNCTION', 'format_time', 'SQL', 'NO_SQL', 'YES', 'INVOKER', 0x7069636F7365636F6E647320544558540A20202020, 0x74657874204348415253455420757466386D623320434F4C4C41544520757466386D62335F67656E6572616C5F6369, , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Takes a raw picoseconds value, and converts it to a human readable form.\n             Picoseconds are the precision that all latency values are printed in\n             within Performance Schema, however are not user friendly when wanting\n             to scan output from the command line.\n             Parameters\n             picoseconds (TEXT):\n               The raw picoseconds value to convert.\n             Returns\n             TEXT\n             Example\n             mysql> select format_time(342342342342345);\n             +------------------------------+\n             | format_time(342342342342345) |\n             +------------------------------+\n             | 00:05:42                     |\n             +------------------------------+\n             1 row in set (0.00 sec)\n             mysql> select format_time(342342342);\n             +------------------------+\n             | format_time(342342342) |\n             +------------------------+\n             | 342.34 us              |\n             +------------------------+\n             1 row in set (0.00 sec)\n             mysql> select format_time(34234);\n              +--------------------+\n             | format_time(34234) |\n             +--------------------+\n             | 34.23 ns           |\n             +--------------------+\n             1 row in set (0.00 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'extract_table_from_file_name', 'FUNCTION', 'extract_table_from_file_name', 'SQL', 'NO_SQL', 'YES', 'INVOKER', 0x70617468205641524348415228353132290A20202020, 0x7661726368617228363429204348415253455420757466386D623320434F4C4C41544520757466386D62335F67656E6572616C5F6369, 0x424547494E0A2020202052455455524E204C45465428535542535452494E475F494E444558285245504C41434528535542535452494E475F494E444558285245504C41434528706174682C20275C5C272C20272F27292C20272F272C202D31292C20274030303234272C20272427292C20272E272C2031292C203634293B0A454E44, 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Takes a raw file path, and extracts the table name from it.\n             Useful for when interacting with Performance Schema data\n             concerning IO statistics, for example.\n             Parameters\n             path (VARCHAR(512)):\n               The full file path to a data file to extract the table name from.\n             Returns\n             VARCHAR(64)\n             Example\n             mysql> SELECT sys.extract_table_from_file_name(\'/var/lib/mysql/employees/employee.ibd\');\n             +---------------------------------------------------------------------------+\n             | sys.extract_table_from_file_name(\'/var/lib/mysql/employees/employee.ibd\') |\n             +---------------------------------------------------------------------------+\n             | employee                                                                  |\n             +---------------------------------------------------------------------------+\n             1 row in set (0.02 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', 0x424547494E0A2020202052455455524E204C45465428535542535452494E475F494E444558285245504C41434528535542535452494E475F494E444558285245504C41434528706174682C20275C272C20272F27292C20272F272C202D31292C20274030303234272C20272427292C20272E272C2031292C203634293B0A454E44, 'NONE');
INSERT INTO `proc` VALUES ('sys', 'ps_is_account_enabled', 'FUNCTION', 'ps_is_account_enabled', 'SQL', 'READS_SQL_DATA', 'YES', 'INVOKER', 0x696E5F686F73742056415243484152283630292C0A2020202020202020696E5F757365722056415243484152283136290A20202020, 0x656E756D2827594553272C274E4F2729204348415253455420757466386D623320434F4C4C41544520757466386D62335F67656E6572616C5F6369, , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Determines whether instrumentation of an account is enabled\n             within Performance Schema.\n             Parameters\n             in_host VARCHAR(60):\n               The hostname of the account to check.\n             in_user (VARCHAR(16)):\n               The username of the account to check.\n             Returns\n             ENUM(\'YES\', \'NO\', \'PARTIAL\')\n             Example\n             mysql> SELECT sys.ps_is_account_enabled(\'localhost\', \'root\');\n             +------------------------------------------------+\n             | sys.ps_is_account_enabled(\'localhost\', \'root\') |\n             +------------------------------------------------+\n             | YES                                            |\n             +------------------------------------------------+\n             1 row in set (0.01 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'ps_is_thread_instrumented', 'FUNCTION', 'ps_is_thread_instrumented', 'SQL', 'READS_SQL_DATA', 'NO', 'INVOKER', 0x696E5F636F6E6E656374696F6E5F696420424947494E5420554E5349474E45440A20202020, 0x656E756D2827594553272C274E4F272C27554E4B4E4F574E2729204348415253455420757466386D623320434F4C4C41544520757466386D62335F67656E6572616C5F6369, , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Checks whether the provided connection id is instrumented within Performance Schema.\n             Parameters\n             in_connection_id (BIGINT UNSIGNED):\n               The id of the connection to check.\n             Returns\n             ENUM(\'YES\', \'NO\', \'UNKNOWN\')\n             Example\n             mysql> SELECT sys.ps_is_thread_instrumented(CONNECTION_ID());\n             +------------------------------------------------+\n             | sys.ps_is_thread_instrumented(CONNECTION_ID()) |\n             +------------------------------------------------+\n             | YES                                            |\n             +------------------------------------------------+\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'quote_identifier', 'FUNCTION', 'quote_identifier', 'SQL', 'NO_SQL', 'YES', 'INVOKER', 0x696E5F6964656E7469666965722054455854, 0x74657874204348415253455420757466386D623320434F4C4C41544520757466386D62335F67656E6572616C5F6369, 0x424547494E0A2020202052455455524E20434F4E434154282760272C205245504C41434528696E5F6964656E7469666965722C202760272C2027606027292C20276027293B0A454E44, 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Takes an unquoted identifier (schema name, table name, etc.) and\n             returns the identifier quoted with backticks.\n             Parameters\n             in_identifier (TEXT):\n               The identifier to quote.\n             Returns\n             TEXT\n             Example\n             mysql> SELECT sys.quote_identifier(\'my_identifier\') AS Identifier;\n             +-----------------+\n             | Identifier      |\n             +-----------------+\n             | `my_identifier` |\n             +-----------------+\n             1 row in set (0.00 sec)\n             mysql> SELECT sys.quote_identifier(\'my`idenfier\') AS Identifier;\n             +----------------+\n             | Identifier     |\n             +----------------+\n             | `my``idenfier` |\n             +----------------+\n             1 row in set (0.00 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', 0x424547494E0A2020202052455455524E20434F4E434154282760272C205245504C41434528696E5F6964656E7469666965722C202760272C2027606027292C20276027293B0A454E44, 'NONE');
INSERT INTO `proc` VALUES ('sys', 'version_major', 'FUNCTION', 'version_major', 'SQL', 'NO_SQL', 'NO', 'INVOKER', '', 0x74696E79696E7428332920756E7369676E6564, 0x424547494E0A2020202052455455524E20535542535452494E475F494E44455828535542535452494E475F494E4445582856455253494F4E28292C20272D272C2031292C20272E272C2031293B0A454E44, 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Returns the major version of MySQL Server.\n             Returns\n             TINYINT UNSIGNED\n             Example\n             mysql> SELECT VERSION(), sys.version_major();\n             +--------------------------------------+---------------------+\n             | VERSION()                            | sys.version_major() |\n             +--------------------------------------+---------------------+\n             | 5.7.9-enterprise-commercial-advanced | 5                   |\n             +--------------------------------------+---------------------+\n             1 row in set (0.00 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', 0x424547494E0A2020202052455455524E20535542535452494E475F494E44455828535542535452494E475F494E4445582856455253494F4E28292C20272D272C2031292C20272E272C2031293B0A454E44, 'NONE');
INSERT INTO `proc` VALUES ('sys', 'format_bytes', 'FUNCTION', 'format_bytes', 'SQL', 'NO_SQL', 'YES', 'INVOKER', 0x627974657320544558540A20202020, 0x74657874204348415253455420757466386D623320434F4C4C41544520757466386D62335F67656E6572616C5F6369, , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Takes a raw bytes value, and converts it to a human readable format.\n             Parameters\n             bytes (TEXT):\n               A raw bytes value.\n             Returns\n             TEXT\n             Example\n             mysql> SELECT sys.format_bytes(2348723492723746) AS size;\n             +----------+\n             | size     |\n             +----------+\n             | 2.09 PiB |\n             +----------+\n             1 row in set (0.00 sec)\n             mysql> SELECT sys.format_bytes(2348723492723) AS size;\n             +----------+\n             | size     |\n             +----------+\n             | 2.14 TiB |\n             +----------+\n             1 row in set (0.00 sec)\n             mysql> SELECT sys.format_bytes(23487234) AS size;\n             +-----------+\n             | size      |\n             +-----------+\n             | 22.40 MiB |\n             +-----------+\n             1 row in set (0.00 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'ps_is_consumer_enabled', 'FUNCTION', 'ps_is_consumer_enabled', 'SQL', 'READS_SQL_DATA', 'YES', 'INVOKER', 0x696E5F636F6E73756D65722076617263686172283634290A202020, 0x656E756D2827594553272C274E4F2729204348415253455420757466386D623320434F4C4C41544520757466386D62335F67656E6572616C5F6369, , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Determines whether a consumer is enabled (taking the consumer hierarchy into consideration)\n             within the Performance Schema.\n             Parameters\n             in_consumer VARCHAR(64):\n               The name of the consumer to check.\n             Returns\n             ENUM(\'YES\', \'NO\')\n             Example\n             mysql> SELECT sys.ps_is_consumer_enabled(\'events_stages_history\');\n             +-----------------------------------------------------+\n             | sys.ps_is_consumer_enabled(\'events_stages_history\') |\n             +-----------------------------------------------------+\n             | NO                                                  |\n             +-----------------------------------------------------+\n             1 row in set (0.00 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'version_patch', 'FUNCTION', 'version_patch', 'SQL', 'NO_SQL', 'NO', 'INVOKER', '', 0x74696E79696E7428332920756E7369676E6564, 0x424547494E0A2020202052455455524E20535542535452494E475F494E44455828535542535452494E475F494E4445582856455253494F4E28292C20272D272C2031292C20272E272C202D31293B0A454E44, 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Returns the patch release version of MySQL Server.\n             Returns\n             TINYINT UNSIGNED\n             Example\n             mysql> SELECT VERSION(), sys.version_patch();\n             +--------------------------------------+---------------------+\n             | VERSION()                            | sys.version_patch() |\n             +--------------------------------------+---------------------+\n             | 5.7.9-enterprise-commercial-advanced | 9                   |\n             +--------------------------------------+---------------------+\n             1 row in set (0.00 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', 0x424547494E0A2020202052455455524E20535542535452494E475F494E44455828535542535452494E475F494E4445582856455253494F4E28292C20272D272C2031292C20272E272C202D31293B0A454E44, 'NONE');
INSERT INTO `proc` VALUES ('sys', 'ps_setup_enable_consumer', 'PROCEDURE', 'ps_setup_enable_consumer', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', 0x0A2020202020202020494E20636F6E73756D6572205641524348415228313238290A20202020, '', , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Enables consumers within Performance Schema\n             matching the input pattern.\n             Parameters\n             consumer (VARCHAR(128)):\n               A LIKE pattern match (using \"%consumer%\") of consumers to enable\n             Example\n             To enable all consumers:\n             mysql> CALL sys.ps_setup_enable_consumer(\'\');\n             +-------------------------+\n             | summary                 |\n             +-------------------------+\n             | Enabled 10 consumers    |\n             +-------------------------+\n             1 row in set (0.02 sec)\n             Query OK, 0 rows affected (0.02 sec)\n             To enable just \"waits\" consumers:\n             mysql> CALL sys.ps_setup_enable_consumer(\'waits\');\n             +-----------------------+\n             | summary               |\n             +-----------------------+\n             | Enabled 3 consumers   |\n             +-----------------------+\n             1 row in set (0.00 sec)\n             Query OK, 0 rows affected (0.00 sec)\n             ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
INSERT INTO `proc` VALUES ('sys', 'list_add', 'FUNCTION', 'list_add', 'SQL', 'CONTAINS_SQL', 'YES', 'INVOKER', 0x696E5F6C69737420544558542C0A2020202020202020696E5F6164645F76616C756520544558540A20202020, 0x74657874204348415253455420757466386D623320434F4C4C41544520757466386D62335F67656E6572616C5F6369, , 'mariadb.sys@localhost', '2024-06-05 16:48:02', '2024-06-05 16:48:02', '', '\n             Description\n             Takes a list, and a value to add to the list, and returns the resulting list.\n             Useful for altering certain session variables, like sql_mode or optimizer_switch for instance.\n             Parameters\n             in_list (TEXT):\n               The comma separated list to add a value to\n             in_add_value (TEXT):\n               The value to add to the input list\n             Returns\n             TEXT\n             Example\n             mysql> select @@sql_mode;\n             +-----------------------------------------------------------------------------------+\n             | @@sql_mode                                                                        |\n             +-----------------------------------------------------------------------------------+\n             | ONLY_FULL_GROUP_BY,STRICT_TRANS_TABLES,NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION |\n             +-----------------------------------------------------------------------------------+\n             1 row in set (0.00 sec)\n             mysql> set sql_mode = sys.list_add(@@sql_mode, \'ANSI_QUOTES\');\n             Query OK, 0 rows affected (0.06 sec)\n             mysql> select @@sql_mode;\n             +-----------------------------------------------------------------------------------------------+\n             | @@sql_mode                                                                                    |\n             +-----------------------------------------------------------------------------------------------+\n             | ANSI_QUOTES,ONLY_FULL_GROUP_BY,STRICT_TRANS_TABLES,NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION |\n             +-----------------------------------------------------------------------------------------------+\n             1 row in set (0.00 sec)\n            ', 'utf8mb3', 'utf8mb3_general_ci', 'utf8mb3_general_ci', , 'NONE');
